#include <stdint.h>

/* Include memory map of our MCU */
#include <stm32l475xx.h>

/* Include drivers */
#include "leds.h"
#include "timer.h"

#if !defined(__SOFT_FP__) && defined(__ARM_FP)
  #warning "FPU is not initialized, but the project is compiling for an FPU. Please initialize the FPU before use."
#endif

// preamble + PID with two bit chunks put in reverse order
#define REVERSE_TWO_PPID	0b110011010101000001100110
#define PPID_MASK			0b000000000000000000000011

// Globals
volatile int interrupt_flag = 0;

//Timer 2 interrupt handler: timer 2 reaches its auto-reload value (ARR)
void TIM2_IRQHandler()
{
	// set software interrupt flag
	interrupt_flag = 1;
	// clear hardware timer interrupt flag
	TIM2->SR &= ~TIM_SR_UIF;
}

void blink_ppid()
{
	static int reverse_two_ppid_var = REVERSE_TWO_PPID;

	// blink next two bits
	leds_set(reverse_two_ppid_var & PPID_MASK);
	// shift to set up bits for next blink
	reverse_two_ppid_var = reverse_two_ppid_var >> 2;
	if(!reverse_two_ppid_var)	// WARNING: only works because my PID does not end in 0b00
	{
		// if all bits have been shifted out, reload
		reverse_two_ppid_var = REVERSE_TWO_PPID;
	}
}

int main(void)
{
//  __enable_irq();
  leds_init();

  timer_init(TIM2);

  timer_set_ms(TIM2, 49);

  while(1)
  {
	  if(interrupt_flag)
	  {
		  blink_ppid();
		  // clear software interrupt flag
		  interrupt_flag = 0;
	  }
  }
}
